import React from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { useNavigate } from 'react-router-dom'
import Timeline from './Timeline'; // Assuming you have a Timeline.js component in the same directory

function App() {
  // Sample data for repositories
  const repositories = [
    { name: 'arXiv', progress: 65 },
    { name: 'PubMed', progress: 50 },
    { name: 'BioRiv', progress: 35 },
    { name: 'MedRxiv', progress: 85 }
  ];
  const navigate = useNavigate()

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="App-logo">Accessibility Hub</div>
          <div className="intro-and-filter">
            <div className="intro-text">
              <p>Welcome!</p>
              <p>This is the accessibility score of PDFs in these repositories</p>
            </div>
            <button className="filter-button">+ Filter</button>
          </div>
        </div>
      </header>

      <div classname="content">

        <div className="repo-list">
          {/* Map through the repositories and display each one */}
          {repositories.map((repo, index) => (
            <div key={index} className="repo-item">
              <span>{repo.name}</span> {/* Repository Name */}
              <div className="progress-bar">
                {/* Dynamically set the width of the progress bar based on the progress value */}
                <div className="progress" style={{ width: `${repo.progress}%` }}></div>
              </div>
              <button>Details</button> {/* Details button */}
            </div>
          ))}
          <button classname="timeline-btn" >Check Timeline</button>
          <Routes>
            <Route path="/" element={<App />} /> {/* Assuming Home is your main component */}
            <Route path="/timeline" element={<Timeline />} />
          </Routes>
        </div>

      </div>

      <footer className="footer">
        <p>Have any problems with our APP?</p>
        <a href="#" className="App-link">Contact Us</a>
        <p>Data Privacy Statement</p>
        <p>LOGO?</p>
      </footer>
    </div>
  );
}

export default App;
