import React, { useState, useEffect }from 'react';
import './App.css';
import Papa from 'papaparse';
import { useNavigate } from 'react-router-dom';
import Root from './Root';

function App() {
  // Sample data for repositories
  /*const repositories = [
    { name: 'arXiv', progress: 65 },
    { name: 'PubMed', progress: 50 },
    { name: 'BioRiv', progress: 35 },
    { name: 'MedRxiv', progress: 85 }
  ];*/
  // const navigate = useNavigate();
  const [avgScores, setAvgScores] = useState([]);

    useEffect(() => {
        // Load and parse the CSV file
        Papa.parse('/data.csv', {
            download: true,
            header: true,
            complete: (result) => {
                const data = result.data;
                let repositoryScores = {};

                // Aggregate the scores for each repository
                data.forEach(row => {
                    const repository = row.Repository;
                    const score = parseFloat(row.File_Score);
                    if (!repositoryScores[repository]) {
                        repositoryScores[repository] = {
                            totalScore: 0,
                            count: 0
                        };
                    }
                    repositoryScores[repository].totalScore += score;
                    repositoryScores[repository].count += 1;
                });

                // Calculate the average score for each repository
                let averageScores = [];
                for (let repo in repositoryScores) {
                    averageScores.push({
                        name: repo,
                        progress: ((repositoryScores[repo].totalScore / repositoryScores[repo].count) * 100).toFixed(2)
                    });
                }

                setAvgScores(averageScores);
            }
        });
    }, []);



  return (

      <div className="App">
        <header className="App-header">
          <div className="header-content">
            <div className="App-logo">Accessibility Hub</div>
            <div className="intro-and-filter">
              <div className="intro-text">
                <p>Welcome!</p>
                <p>This is the accessibility score of PDFs in these repositories</p>
              </div>
              <button className="filter-button">+ Filter</button>
            </div>
          </div>
        </header>

        <div classname="content">

          <div className="repo-list">
            {/* Map through the repositories and display each one */}
            {avgScores.map((repo, index) => (
              <div key={index} className="repo-item">
                <span>{repo.name}</span> {/* Repository Name */}
                <div className="progress-bar">
                  {/* Dynamically set the width of the progress bar based on the progress value */}
                  <div className="progress" style={{ width: `${repo.progress}%` }}></div>
                </div>
                <button>Details</button> {/* Details button */}
              </div>
            ))}
              <button className="timeline-btn" >Check Timeline</button>
          </div>

        </div>

        <footer className="footer">
          <p>Have any problems with our APP?</p>
          <a href="#" className="App-link">Contact Us</a>
          <p>Data Privacy Statement</p>
          <p>LOGO?</p>
        </footer>
      </div>

  );
}

export default App;
